CREATE TABLE `tb_task_nodes` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `taskid` int(11) NOT NULL DEFAULT '0' COMMENT '任务标题ID',
  `creatorid` int(11) NOT NULL DEFAULT '0' COMMENT '创建人ID',
  `createtime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '任务创建时间',
  `parentid` int(11) NOT NULL DEFAULT '0' COMMENT '父级任务节点',
  `ancestorids` varchar(256) NOT NULL DEFAULT '' COMMENT '祖先ids  |1|2|3|',
  `level` tinyint(4) NOT NULL DEFAULT '0' COMMENT '层级',
  `isleaf` bit(1) NOT NULL DEFAULT b'1' COMMENT '是否叶子节点  ',
  `title` varchar(128) NOT NULL DEFAULT '' COMMENT '任务节点标题',
  `employeeid` int(11) NOT NULL DEFAULT '0' COMMENT '负责人ID',
  `content` varchar(256) NOT NULL DEFAULT '' COMMENT '任务说明',
  `details` varchar(1024) DEFAULT '' COMMENT '详细任务说明-参与人的任务说明（可空）',
  `isimportant` bit(1) NOT NULL DEFAULT b'0' COMMENT '重要程度，1重要，0普通',
