  `remindway` varchar(64) NOT NULL DEFAULT '0' COMMENT '通知方式，1消息，2邮件，12邮件和消息',
  `leadtime` int(11) NOT NULL DEFAULT '0' COMMENT '提前N时间通知。分钟数',
  `repetition` int(11) NOT NULL DEFAULT '0' COMMENT '重复提醒的 间隔分钟数',
  `ishasfile` bit(1) NOT NULL DEFAULT b'0' COMMENT '是否有附件,0:没有;1:有',
  `status` tinyint(4) NOT NULL DEFAULT '0' COMMENT '状态，0默认正常，1完成(预留)，2草稿，3删除，4拒绝 5 提交待审核 6 关闭',
  `starttime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '开始时间',
  `expectendtime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '预期结束时间',
  `realendtime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '实际结束时间',
  `log` text NOT NULL COMMENT '任务反馈信息  包括审核信息和拒绝说明 JSON数据',
  `clienttype` tinyint(4) NOT NULL DEFAULT '0' COMMENT '标记动态产生的来源 0网页 1 IOS 2安卓 3WP 4桌面客户端',
  PRIMARY KEY (`id`)
